#include <string>
#include <iostream>

using namespace std;

class MySolution {
public:
    bool canChange(string start, string target) {
        int N = target.size();
        int j = 0, i = 0;
        while(i < N && j < N){
            while(target[j] == '_')
                ++j;
            if(target[j] == 'R'){
                if(i <= j && start[i] == 'R'){
                    ++j;
                }else if(i > j || start[i] == 'L'){
                    break;
                }
                ++i;
            }else{
                if(i >= j && start[i] == 'L')
                    ++j;
                else if(i < j || start[i] == 'R')
                    break;
                ++i;
            }
        }
        while(i < N){
            if(start[i] == '_')
                ++i;
            else
                break;
        }
        while(j < N){
            if(target[j] == '_')
                ++j;
            else
                break;
        }
        return j == N && i == N;
    }
};

// @lc code=start
class Solution {
public:
    bool canChange(string start, string target) {
        int N = target.size();
        int j = 0, i = 0;
        for(; i < N || j < N; ++i, ++j){
            while(i < N && start[i] == '_')
                ++i;
            while(j < N && target[j] == '_')
                ++j;
            if(i == N || j == N || start[i] != target[j] || (start[i] == 'L' && i < j) || (start[i]=='R' && i > j))
                break;
        }
        return j == N && i == N;
    }
};
